// actions.js
export const addTodo = (text) => ({
    type: 'ADD_TODO',
    payload: text
  });
  
  export const deleteTodo = (index) => ({
    type: 'DELETE_TODO',
    payload: index
  });
  
  export const toggleTodo = (index) => ({
    type: 'TOGGLE_TODO',
    payload: index
  });
